import React from 'react'
import { Text, View } from 'react-native'
import tw from "twrnc";

/*
Functional component that will map JSON data to UI Component.
*/

export default function ListItem(props) {
    return (    
    <View
    style={tw`bg-blue-500 w-96 p-4 my-1`}
    >
        <Text style={tw`text-base my-2 font-medium`}>{props.order_status}</Text>
        <Text style={tw`text-xl mb-1 font-bold`}>{props.pharmacy_name}</Text>
        <Text style={tw`text-base`}>Sum Total : R{props.order_total}</Text>
    </View>
  )
}
